module DisplayHelper
  def display_welcome
    puts brain
    puts big_brain_time
    puts "Welcome to Flash Card Manager CLI!"
    puts "This app helps you manage your flash cards to maximize your learning."
    puts "This application connects to your Sinatra API."
    puts "Make sure your API server is running on http://localhost:9292"
    puts "Use ↑/↓ arrow keys to navigate and ENTER to select.\n\n"
  end

  def display_card_preview(card)
    puts "┌─────────────────────────────────────┐"
    puts "│ FRONT:                              │"
    puts "│ #{card['front'][0..35].ljust(35)} │"
    puts "├─────────────────────────────────────┤"
    puts "│ BACK:                               │"
    puts "│ #{card['back'][0..35].ljust(35)} │"
    puts "└─────────────────────────────────────┘"
  end

  def display_error(message)
    @prompt.error("Error: #{message}")
  end

  def display_success(message)
    @prompt.success(message.to_s)
  end

  def display_info(message)
    @prompt.say(message.to_s, color: :blue)
  end

  def display_warning(message)
    @prompt.warn(message.to_s)
  end

  def pause_for_user
    puts "\nPress Enter to continue..."
    gets
  end

